mapper.select:
select * from account where 1=1

	and name = #{account}


	and no = #{host}

select a.No,a.TmpNo,a.TmpNextIntent,''as'보정다음인텐트',a.main,a.name,a.next,b.answer
from intent a inner join answer b on a.account = b.account and a.answer = b.no
where a.account = #{account}
and a.Language = #{language}
order by TmpNo

select a.No
from intent a inner join answer b on a.account = b.account and a.answer = b.no
where a.account = #{account}
and a.Language = #{language }
and a.TmpNo = #{num}

update.foreach:
update Intent
set next = #{item.next}
where no = #{item.no}

mapper.select:
select a.no, b.id from account a, backendInfo b where a.no = b.host and b.service = 'itf'

	and a.name = #{account}


	and a.no = #{host}

SELECT a.No, a.Name, b.Sentence FROM BertIntent a LEFT OUTER JOIN BertSentence b ON a.No = b.BertIntentNo WHERE 1=1 AND a.No = (SELECT a.No FROM BertIntent a, BackendInfo b WHERE a.BertItfId = b.id AND a.Name = #{bertIntentName} AND a.Language = #{lang} AND b.host = #{host}) AND a.Language = #{lang}
AND b.Sentence like CONCAT('%', #{searchSentence}, '%')
ORDER BY b.Sentence OFFSET #{startRow} ROWS FETCH NEXT ${endRow} ROWS ONLY

SELECT f.BertIntentNo, f.Name, f.RegexIntentNo, f.Regex AS Regex, SUBSTRING((SELECT '@' + a.RuleValue + '#', b.RuleType AS [text()] FROM RegexRule a, RegexRuleType b WHERE a.RuleTypeNo = b.No AND a.RegexIntentNo = f.RegexIntentNo ORDER BY a.No FOR XML PATH ('')), 2, 1000) AS RegexRule FROM ( SELECT a.No As BertIntentNo, a.Name, b.No As RegexIntentNo, b.Regex FROM BertIntent a LEFT OUTER JOIN RegexIntent b ON a.No = b.BertIntentNo LEFT OUTER JOIN RegexRule c ON b.No = c.RegexIntentNo WHERE 1=1
AND a.No = #{bertIntentNo}
AND a.No = (SELECT a.No FROM BertIntent a, BackendInfo b WHERE a.BertItfId = b.id AND a.Name = #{bertIntentName} AND a.Language = #{lang} AND b.host = #{host})
AND a.Language = #{lang}
AND (b.Regex like CONCAT('%', #{searchSentence}, '%') OR c.RuleValue like CONCAT('%', #{searchSentence}, '%'))
GROUP BY a.No, a.Name, b.No, b.Regex ORDER BY b.No OFFSET #{startRow} ROWS FETCH NEXT ${endRow} ROWS ONLY) f

mapper.delete:
DELETE FROM BertIntent FROM BertIntent a INNER JOIN BackendInfo b ON a.BertItfId = b.id WHERE a.Name = #{bertIntentName} AND b.host = #{host} AND b.service = 'ITF' AND a.Language IN
#{item}
DELETE FROM IntentRel FROM IntentRel a INNER JOIN BertIntent b ON a.BertIntent = b.No INNER JOIN BackendInfo c ON b.BertItfId = c.id WHERE b.Name = #{bertIntentName} AND c.host = #{host} AND c.service = 'ITF' AND b.Language IN
#{item}

mapper.select:
SELECT COUNT(DISTINCT b.No) FROM BertIntent a LEFT OUTER JOIN RegexIntent b ON a.No = b.BertIntentNo LEFT OUTER JOIN RegexRule c ON b.No = c.RegexIntentNo WHERE 1=1
AND a.No = #{bertIntentNo}
AND a.No = (SELECT a.No FROM BertIntent a, BackendInfo b WHERE a.BertItfId = b.id AND a.Name = #{bertIntentName} AND a.Language = #{lang} AND b.host = #{host})
AND a.Language = #{lang}
AND (b.Regex like CONCAT('%', #{searchSentence}, '%') OR c.RuleValue like CONCAT('%', #{searchSentence}, '%'))

mapper.delete:
DELETE FROM RegexRule
WHERE RegexRule.RegexIntentNo
  in (SELECT RegexRule.RegexIntentNo FROM RegexIntent
	  WHERE RegexIntent.Host = #{host}
)

mapper.update:
UPDATE RegexIntent
SET Regex = #{regex}
WHERE No = #{regexIntentNo}

