mapper.select:
        SELECT TOP 1 A.*
 		  FROM ChatbotLog.dbo.intentLog A
 		  LEFT OUTER JOIN ChatbotLog.dbo.sessionLog B
   			ON A.sessionId = B.id
 		 WHERE B.session=#{roomSession}
		   AND A.utter = #{utter}
      ORDER BY id DESC;

mapper.delete:
