mapper.select:
SELECT No, Answer
FROM Intent
WHERE Account=#{nowDelHost}

SELECT id
FROM BackendInfo
WHERE host=#{nowDelHost}

SELECT No
FROM BertIntent
WHERE ${backendInfoStr}

SELECT No
FROM RegexIntent
WHERE Host = #{nowDelHost}

SELECT COUNT(*) 
  FROM ReplaceDict 
 WHERE Host = ${host} 
   AND Lang = #{lang}

   AND (Before LIKE CONCAT('%', #{searchReplaceDict}, '%')
	OR After LIKE CONCAT('%', #{searchReplaceDict}, '%'))

SELECT ROW_NUMBER() OVER(
ORDER BY ${orderCol} ${orderSort}
ORDER BY No desc
) AS RNUM , No , Host , Lang , Before , After FROM ReplaceDict WHERE Host = #{host} AND Lang = #{lang}
AND (Before LIKE CONCAT('%', #{searchReplaceDict}, '%') OR After LIKE CONCAT('%', #{searchReplaceDict}, '%'))
ORDER BY ${orderCol} ${orderSort}
ORDER BY No desc
OFFSET #{startRow} ROWS FETCH NEXT #{endRow} ROWS ONLY

